mapper.select:
SELECT COUNT(*)
FROM CODE_TB
WHERE CODE_GRP_ID = #{code_grp_id}
AND CODE_VAL = #{code_val}
AND USE_YN = 'Y'

mapper.update:
UPDATE CODE_TB
SET UPDATED_AT = now()
    ,UPDATED_ID = #{updated_id}
    ,CODE_NM = #{code_nm}
    ,BIGO = #{bigo}
    ,ORD = #{ord}
WHERE CODE_ID = #{code_id}

UPDATE CODE_TB
SET UPDATED_AT = now()
  , UPDATED_ID = #{updated_id}
  , USE_YN =  #{use_yn}
WHERE CODE_ID = #{code_id}
