mapper.select:
SELECT  COUNT(*)
FROM CODE_GRP_TB
WHERE CODE_GRP_VAL = #{code_grp_val}

mapper.update:
UPDATE CODE_GRP_TB
SET UPDATED_AT = now()
    , UPDATED_ID = #{updated_id}
    , CODE_GRP_NM = #{code_grp_nm}
WHERE CODE_GRP_ID = #{code_grp_id}

UPDATE CODE_GRP_TB
SET UPDATED_AT = now()
    , UPDATED_ID = #{updated_id}
    , USE_YN =  #{use_yn}
WHERE CODE_GRP_ID = #{code_grp_id}
